import { configureStore } from '@reduxjs/toolkit';
import todoReducer from './todoSlice';
import { loadFromLocalStorage, saveToLocalStorage } from '../utils/localStorage';

const defaultTodos = [
  {
    id: "random-id-1",
    title: "todo 1",
    completed: true,
    dueDate: "2024-12-05",
  },
  {
    id: "random-id-2",
    title: "todo 2",
    completed: false,
    dueDate: "2024-12-10",
  },
];

const persistedState = loadFromLocalStorage() && { items: defaultTodos, filter: 'all' };

const store = configureStore({
  reducer: {
    todos: todoReducer,
  },
  preloadedState: persistedState, // Load state from localStorage or defaultTodos
});

store.subscribe(() => {
  saveToLocalStorage(store.getState().todos); // Save todos state to localStorage on every change
});

export default store;
