import { configureStore } from '@reduxjs/toolkit';
import todoReducer from './todoSlice';
import { loadFromLocalStorage, saveToLocalStorage } from '../utils/localStorage';

const defaultTodos = [
  {
    id: "random-id-1",
    title: "todo 1",
    completed: true,
    dueDate: "2024-12-05",
  },
  {
    id: "random-id-2",
    title: "todo 2",
    completed: false,
    dueDate: "2024-12-10",
  },
];

// Load the existing todos from localStorage
const storedTodos = loadFromLocalStorage();

// If there are stored todos in localStorage, use them; otherwise, use defaultTodos
const initialState = {
  items: storedTodos ? [...storedTodos, ...defaultTodos] : defaultTodos, // Combine both stored and default todos
  filter: 'all', // This can be 'all', 'completed', 'pending', 'overdue', etc.
};

const store = configureStore({
  reducer: {
    todos: todoReducer,
  },
  preloadedState: initialState, // Initialize with merged state
});

store.subscribe(() => {
  saveToLocalStorage(store.getState().todos); // Save updated todos to localStorage on every change
});

export default store;
