// store.js
import { configureStore } from '@reduxjs/toolkit';
import todoReducer from './todoSlice';
import { saveToLocalStorage, loadFromLocalStorage } from './utils/localStorage';

const persistedState = loadFromLocalStorage(); // Load persisted state from localStorage

const store = configureStore({
  reducer: {
    todos: todoReducer,
  },
  preloadedState: persistedState, // Set the initial state from localStorage (if exists)
});

// Subscribe to store changes and save state to localStorage
store.subscribe(() => {
  saveToLocalStorage(store.getState()); // Save state to localStorage whenever state changes
});

export default store;
