import { configureStore } from '@reduxjs/toolkit';
import todoReducer from './todoSlice';
import { loadFromLocalStorage, saveToLocalStorage } from '../utils/localStorage';

const defaultTodos = [
	{
		id: "random-id-1",
		title: "todo 1",
		completed: true,
		dueDate: "2024-12-05",
	},
	{
		id: "random-id-2",
		title: "todo 2",
		completed: false,
		dueDate: "2024-12-10",
	},
];

// Load todos from localStorage
const persistedState = loadFromLocalStorage() || { items: [], filter: 'all' };

// Combine defaultTodos with persistedTodos, ensuring no duplicates by ID
const combinedTodos = [
	...defaultTodos,
	...persistedState.items.filter(
		persistedTodo => !defaultTodos.some(todo => todo.id === persistedTodo.id)
	),
];

const store = configureStore({
	reducer: {
		todos: todoReducer,
	},
	preloadedState: {
		items: combinedTodos, // Combine both default and persisted todos
		filter: persistedState.filter || 'all', // Ensure filter state is also handled
	},
});

store.subscribe(() => {
	saveToLocalStorage(store.getState().todos); // Save todos state to localStorage on every change
});

export default store;
